package com.revature.converters;

public class InventoryLevel {
	
	private int id;
	
	private String name;
	
	private String description;
	
	private int levelId;

	public InventoryLevel() {
		super();
		// TODO Auto-generated constructor stub
	}

	public InventoryLevel(int id, String name, String description, int levelId) {
		super();
		this.id = id;
		this.name = name;
		this.description = description;
		this.levelId = levelId;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getDescription() {
		return description;
	}

	public void setDescription(String description) {
		this.description = description;
	}

	public int getLevelId() {
		return levelId;
	}

	public void setLevelId(int levelId) {
		this.levelId = levelId;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((description == null) ? 0 : description.hashCode());
		result = prime * result + id;
		result = prime * result + levelId;
		result = prime * result + ((name == null) ? 0 : name.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		InventoryLevel other = (InventoryLevel) obj;
		if (description == null) {
			if (other.description != null)
				return false;
		} else if (!description.equals(other.description))
			return false;
		if (id != other.id)
			return false;
		if (levelId != other.levelId)
			return false;
		if (name == null) {
			if (other.name != null)
				return false;
		} else if (!name.equals(other.name))
			return false;
		return true;
	}

	@Override
	public String toString() {
		return "InventoryLevel [id=" + id + ", name=" + name + ", description=" + description + ", levelId=" + levelId
				+ "]";
	}
}
